css:
@-moz-document domain("kaskus.co.id") {

/*\
*
* @name			Kaskus Minimum
* @description	        comfort for your eye.
* @author		DijaminGila
* @version              0.1
*
\*/

/* general */
  .bottom-frame, .kasad-h, #c-thread, #sell, .banner-top-ads, .col-sm-3.sidebar, .row.sidebar-wrap, .related-thread.nongol, .popover.fade.bottom.in, .kaskus-ads.postlist, .row.ads-lb-wrap, .col-sm-12.banner-frame, .user-control-stick, #thread_post_list > .row > .col-xs-12 > .text-center, .skin, #feedback, .kaskus-ads .postlist {
    display:none !important;
  }
  
  /* thread view */
  .main-content {
  	width: 80%; 
    margin-left:auto;
    margin-right:auto;
    float:none;
  }
  .user-control-stick { width: 1170px; }
  
  /* ucp */
  .user-home-content table.post-entry tbody td:nth-child(1) { width: 5%; }
  .user-home-content table.post-entry tbody td:nth-child(2) { width: 65%; }
  .user-home-content table.post-entry tbody td:nth-child(3) { width: 10%; }
  .user-home-content table.post-entry tbody td:nth-child(4) { width: 10%; }
  .user-home-content table.post-entry tbody .author { float: left; width: auto; }
  .user-home-content table.post-entry tbody .act { float: right; margin-top: -16px; }
  
  .user-home-content table:not(.post-entry) tbody td:nth-child(1) { width: 3.3%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(2) { width: 45%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(3) { width: 6.7%; }
  .user-home-content table:not(.post-entry) tbody td:nth-child(4) { width: 35%; }
	
	/* header */
  .site-header {
  	background:#2c3e50;
  }
  .navbar-default .navbar-nav>li>a, .navbar-default .navbar-text {
    color:#ecf0f1;
  }
  .sidebar-trigger {
  background:#2c3e50;
  }
  .header-trigger {
  border:1px solid #34495e;
  }
  #btn-whatsnew {
  color:#ecf0f1;
  }
  a#help-center {
  color:#ecf0f1;
  }
  #left-nav.full-show {
  background:#bdc3c7;
  }

	/*Body*/
  body {
  background:#ecf0f1;
  }

	/*Footer*/
  footer.site-footer {
  background:#2c3e50;
  }
  footer.site-footer h3 {
  color:#ecf0f1;
  }
  footer.site-footer ul li a {
  color:#ecf0f1;
  }
}
