css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linzomir.com.ua") {
  .contentBlock {background: none;}
  .footer {background: none;}
  .headerPick {background: none;}
    
  #menu {height: 20px; background: rgba(0, 0, 0, 0.43); border-radius: 5px; padding: 5px;}
  #header .button-search {border: none;}
  #header #search input {border: none; box-shadow: none;}
    
  #footer {background: rgba(0, 0, 0, 0.43); border-radius: 10px;}

  #menu > ul > li > div {background: rgba(0, 0, 0, 0.43);}

  .product-grid > div {width: 155px;}
  #currency {top: 213px;}

  #menu > ul > li ul > li > a:hover {border-radius: 3px;}
  #welcome a, #welcome a:visited, #welcome a b {color: #fff; font-weight: bold;}
}
