css:
@-moz-document domain("crazyguyonabike.com") {
body {
  font-family: Sans-serif;
  padding: 1.5em;
  line-height: 1.6;
  background-color: rgba(245, 245, 220, 0.7);
}
/*main content area */
div[align] {
  max-width: 800px;
  font-family: Georgia;
}
/*photos within content area*/
div[align] table[bgcolor] {
  font-family: Sans-serif;
}
/*legal*/
#bottom {
	font-size: 10px;
    line-height: 1.4;
}
/* prev / next buttons */
table[cellpadding="5"][cellspacing="3"] td {
  padding: 0; 
}
table[cellpadding="5"][cellspacing="3"] td a,
table[cellpadding="5"][cellspacing="3"] td font {
  padding: 0.6em;
  display: inline-block;
}
nobr a {
  padding: 0 !important;
}
td[bgcolor="beige"], th { 
  padding: 0.4em; 
}
table + b > a {
  display: block;
  margin-top: 1em;
}

}
