@-moz-document url-prefix("https://scratch.mit.edu/discuss/topic") {
  .postmsg a {
    position: relative;
  }

  .postmsg a:hover::after {
    content: attr(href);
    position: absolute;
    bottom: 0;
    left: 0;
    font-size: 12px;
    border-radius: 5px;
    padding: 3px;
    z-index: 100;
    /*[[colorscheme]]*/
  }
}

