css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(.+\\.)?(samlib|budclub).ru/.+/.+.shtml"), regexp("https?://(.+\\.)?(archive).org/web/.+/https?://(.+\\.)?(samlib|budclub).ru/.+/.+.shtml") {
  body {
    color: #000;
    background-color: #ebe2c9 !important;
    margin: 0 60px;
    text-align: justify!important;
    font-size: 1.7em!important;
    font-family:"Trebuchet Ms",sans-serif;
    -webkit-hyphens: auto;
    -moz-hyphens: auto;
    -ms-hyphens: auto;
  }
  body > center:nth-child(2) > nobr:nth-child(2) {
    font-size: 0.7em;
  }
  ul {
    list-style-type: none;
  }
  a {
    text-decoration: none;
    color: #a53d17;
  }
  a:hover {
    color: #916131;
  }
  a:visited {
    color: #714131;
  }
  font {
    font-family:"Trebuchet Ms",sans-serif!important;
    color: #000;
    background-color: #ebe2c9 !important;
  }
  table {
    background-color: #ebe2c9 !important;
  }
 
  body > center > table[style="display: block !important"] >tbody > tr {
    display:none!important;
    height: 0px;
  }

  body > center > table:nth-child(3) {
    display:none!important;
  }

 body > center > table:nth-child(4) {
    padding-top: 1em;
 }

  dd > b:first-child, dd > div[align="center"] > p > b, dd > div[align="center"] > b:first-child, dd > center > strong {
    font-size: 1.1em!important;
    margin: 10px 0px;
    text-shadow: 2px 2px 2px #555, 0 0 4em #aaa;
  }
  dd:first-letter, body > xxx7 > p:nth-child:first-letter {
    text-indent: 2em; 
  }
  img {
    height: auto!important;
    max-width: 100%!important;
    display: inline-block;
    text-align: center;
  }
  xxx7, dd {
    font-size: 1.0em!important;
    font-family:"Trebuchet Ms",sans-serif;
    text-align: justify!important;
    background-color: #ebe2c9 !important;
    
  }
  :not(xxx7) > blockquote {
    font-size: 1.5em!important;
    font-family:"Trebuchet Ms",sans-serif;
    text-align: justify!important;
    background-color: #ebe2c9 !important;
    margin: 0 0;
  }
  body > table > tbody > tr > td > div[align="justify"] {
    font-size: 1.5em!important;
    font-family:"Trebuchet Ms",sans-serif;
    text-align: justify!important;
    background-color: #ebe2c9 !important;
    margin: 0 0;
  }
  h1 {
    font-size: 1.5em!important;
    text-shadow: 2px 2px 2px #555, 0 0 4em #aaa;
    text-align: center;
  }
  h2 {
    font-size: 1.3em!important;
    text-shadow: 2px 2px 2px #555, 0 0 4em #aaa;
  }
  h3, h4 {
    font-size: 1.2em!important;
    text-shadow: 2px 2px 2px #555, 0 0 4em #aaa;
  }
}
