css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(lainchan.org){
  
  *::-moz-selection{background:#00FE00;color:#000;}
  *::selection{background:#00FE00;color:#000;}
  
  body{
    background:#000 !important;
    color:green !important;
  }
  a{
    color:#8725EC !important;
    outline:0 !important;
  }
  a:hover{
    text-shadow:none !important;
    color:yellow !important;
  }
  .post, .reply{
    background:#111 !important;
    border-radius:0 !important;
    border:0 !important;
    margin:2px 0 !important;
  }
  input{
    border-radius:0 !important;
    outline:0 !important;
    border:0 !important;
  }
  input[type="submit"]:hover{
    background:#8725EC !important;
    color:#fff !important;
  }
  span.quote{
    color:red !important;
  }
  span.subject{
    color:yellow !important;
  }
  .bar, .bottom{
    border:0 !important;
  }
  .bottom a{
    padding:1px 0 !important;
  }
  select{
    border:0 !important;
    padding:1px 0 !important;
    background:#111 !important;
  }
  p.intro{
    padding:5px 0;
  }
}
