css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("doit.im") {

    #task_group {
        margin-bottom: 0;
        margin-top:0;
    }
  
    #task_paper {
        margin-top: 6px;
        padding: 0 12px;
    }

    .quickadd-bar .quick {
        padding: 24px 0 7px;
        height: 28px;
    }
    
    #header {
       padding: 0;
    }
    
    input.task-quick-add {
        padding: 6px 100px 6px 12px;
    }
    
    .task-group .groups .group {
       margin-top: 8px;
    }
    
    .toolbar {
        height: 30px;
    }
    #groupby {
        margin-top: 5px;
    }
    #sidebar h3 {
        margin: 0;
    }
    .sidebar li {
        height: 26px;
        line-height: 26px;
    }
    
    .sidebar .side-h3, #sidebar_right .side-title {
        margin: 2px 0 0;
    }
    
    #goal_root {
        display: none;
    }
    
    #context_root {
        display: none;
    }
    
    #task_container > div {
        height: auto !important;
    }
}
