css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org"), domain("wikimedia.org"), domain("wikiquote.org"), domain("wikibooks.org"), domain("wikiversity.org"), domain("wikivoyage.org"), domain("wikidata.org"), domain("wikinews.org"), domain("wikisource.org"), domain("mediawiki.org") {
	body {
		margin: 0 auto;
		max-width: 1200px;
		font-family: sans-serif !important;
	}
	div#mw-head {	
		max-width: 1200px;
		margin: 0 auto;
		left: 0;
	}
	div#mw-panel {
		position: absolute;
		top: 160px;
		left: auto;
		right: auto;
	}
	div#left-navigation {
		margin-left: 176px;
		max-width: 1200px;
	}
	div#mw-page-base {
		margin-left: 176px;
	}
	div#mw-content-text {
		line-height: 20pt;
	}
	p, ol, ul {
		max-width: 38em;
		font-size: 14pt;
	}
	div.vectorTabs li a {
		height: 22px;
	}
}
