css:
@-moz-document domain("readability.com") {
.article .hentry {
	font-family: "ヒラギノ丸ゴ Pro W4","Hiragino Maru Gothic Pro","Meiryo UI","Meiryo",sans-serif !important;
	color: #555 !important;
	text-align: justify !important;
	line-height: 1.4em !important;
	}
.article a {
	color: #555 !important;
	}
.article section h2,
.article section h3,
.article section h4,
.article section h5,
.article section h6 {
	font-size: 1.4em !important;
	line-height: 1.2em !important;
	}
.article section p {
	font-size: 1em !important;
	line-height: 1.5em !important;
	}
.rdb-footnote {
	font-size: .5em !important;
	}
article .rdb-footnote a {
	color: #aaa !important;
	}
li.bookmark {
	font-family: "ヒラギノ丸ゴ Pro W4","Hiragino Maru Gothic Pro","Meiryo UI","Meiryo",sans-serif !important;
	}

}
