css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("www.coosp.etr.u-szeged.hu") {
 
body {
  background-color : #2B2B2B;
}
#wrapper {
  background-color: #494949;
}
#footer {
  background-color: #494949;
  color: #FFFFFF;
}
h1 span.title {
  color: #FFFFFF;
}
div.currentday {
  color: #FFFFFF;
}
table.monthcalendar a{
  color: #FFFFFF;
}
table.monthcalendar th.dayheader {
  color: #FFFFFF;
}
table.monthcalendar tr.week td.weekheader a {
  color: #FFFFFF;
}
table.monthcalendar tr.week td.day a {
  color: #FFFFFF;
}
table.monthcalendar tr.week.hovered {
  background-color: #B5B5B5;
}
aside.left h2 {
  color: #FFFFFF;
}
aside.left li a {
  color: #FFFFFF;
}
section.scenes .code {
  color: #FFFFFF;
}
section.scenes .title a {
  color: #FFFFFF;
}
section.scenes li > div:hover {
    background-color: #B5B5B5;
}
 
/* loginpage */
body.mainbody {
  background-color : #2B2B2B;
}
#loginpage {
  background-color: #494949;
}
#loginbox section.msgs {
  color: #FFFFFF;
}
#loginbox section.msgs h2 a {
  color: unset;
}
#loginbox section.msgs h2 a:hover {
  color: #FF9200;
}
#loginbox #loginleft div.options {
  color: unset;
}
 
}
