css:
@-moz-document domain("shikimori.org") {
body 
{
    display: block !important;
}

.bar.vertical .line 
{
    border-right: 1px solid transparent;
}

.shortened 
{
    height: auto !important;
  	overflow: visible;
}

.b-height_shortener
{
    display: none;
}

.b-catalog_entry.in_list img {
	background-color: #A00;
	border: 1px solid #A00;
	padding: 3px 3px;
	opacity: 0.5;
    max-height: 240px;
}

.b-catalog_entry.in_list img:Hover {
	opacity: 1;
}

.marker {
	display: none;  
}

.completed img , .on_hold img , .watching img , .planned img
{  
	opacity: 0.4;
}

.completed img:Hover, .on_hold img:Hover , .watching img:Hover , .planned img:Hover
{  
	opacity: 1;
}

.b-catalog_entry.planned .image-decor::after, .b-catalog_entry.watching .image-decor::after, .b-catalog_entry.completed .image-decor::after, .b-catalog_entry.rewatching .image-decor::after, .b-catalog_entry.on_hold .image-decor::after, .b-catalog_entry.dropped .image-decor::after
{
  top: 2px !important;
  bottom: auto !important;
}

#animes_collection_index > section > div > div.menu-slide-outer > div > aside > div > div.block.genres > div.b-spoiler > div
{
  display: inline !important;
}

#animes_collection_index > section > div > div.menu-slide-outer > div > aside > div > div.block.genres > div.b-spoiler > label
{  
  display: none !important;
}
}
