css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forecast.io") {
  #forecast_lines h2, #forecast_lines p, #weather_maps h2, #weather_maps p { 
    display: none !important;
  }
  #main {
    margin: 0px !important;
  }
  #forecast {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    height: 900px;
    width: 100%;
    margin: 0px !important;
  }
  #forecast_lines {
    order: 0;
    margin: 0px !important;
  }
  #weather_maps {
    order: 1;
    margin: 0px !important;
  }
  #outlook {
    order: 2;
    margin: 0px !important;
  }
  .overview_container {
    order: 3;
    margin: 0px !important;
  }
  #map {
    order: 4;
    margin: 0px !important;
  }
}
