css:
@-moz-document domain("slack.com") {
/* remove fading during room switching, remove headers from channel list */
#msgs_overlay_div,
#channels_header,
#direct_messages_header {
  display:none !important;
}
/* remove fancy text shadows */
* {
  text-shadow:none !important;
}
/* remove roundness of scrolll bars */
#client-ui .monkey_scroll_handle_inner,
#client-ui .monkey_scroll_bar,
/* remove channel list border radius */
.channels_list_holder ul li a,
#search_terms { border-radius: 0; }
#primary_file_button { background:#fff; }

#search_terms {
  -webkit-transition:none;
}
#header .client_header_button { border:none; }
}
