css:
@-moz-document url("https://twitter.com/") {
body.three-col .content-main
{
    opacity: 0.95;
}

.module ul:last-child li:last-child .list-link,
.module ul:last-child li.last-item .list-link,
.module .list-link.last,
.wtf-module .import-prompt,
.Footer,
.module .contrast-flex-module:last-child
{
    opacity: 0.05;
}

.module ul:last-child li:last-child .list-link:hover,
.module ul:last-child li.last-item .list-link:hover,
.module .list-link.last:hover,
.wtf-module .import-prompt:hover,
.Footer:hover,
.module .contrast-flex-module:last-child:hover
{
    opacity: 0.5;
}


.DashboardProfileCard, .flex-module, .Trends
{
    opacity: 0.6;
}

.DashboardProfileCard:hover, .flex-module:hover, .Trends:hover
{
    opacity: 0.99;
}

.topbar
{
    opacity: 0.6;
}

.topbar:hover
{
    opacity: 0.99;
}

}
