@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kinogo.net"), domain("kinogo.co") {
  
  body {
    background: #000;
  }

  .wrapper {
    padding-top: 0;
  }
  
  .horizontal2 {
    display: none;
  }
  
  .menu {
    position: absolute;
    right: -453px;
    top: -33px;
  }
  
  .leftblok_contener2 {
    position: fixed;
    z-index: 999;
    background: url("http://kinogo.net/templates/kinogo/images/bg.png") repeat-y scroll 0 0 #585858;
  }
  
  .mimiserblock, .leftblok_contener>div, .rek_top, .main_menu {
    display: none;
  }
  
  .leftblok1_header, .header {
    height: 1px;
  }
  
  .topmenu {
    height: 0;
  }
  
  .deepnavigation {
    padding: 0;
  }
  
  .header44 .search_panel {
    width: auto;
  }
  
  .header44 {
    position: fixed;
    top: 0;
    width: 338px;
    margin: 0;
    overflow: visible;
  }
  
  .header44 .search_panel {
    padding: 0 !important;;
  }
  
  .header44 .user_panel {
    float: none;
  }

}
