css:
@-moz-document url-prefix("http://www.sweclockers.com"), url-prefix("https://www.sweclockers.com") {
div .hfix > .mainMenu {
  position: fixed;
  z-index: 201;
  top: 0px;
  left: 0px;
  width: 100%;
}

div .header > .main {
  position: fixed;
  z-index: 200;
  top: 18px;
  left: 0px;
  width: 100%;
  /* height: 56px !important; */
  min-height: 56px !important;
}

div .header > .mainMenu > .shadow {
  padding-top: 2px !important;
}

div .hfix > .banner {
  margin-top: 75px !important;
  margin-bottom: 35px !important;
}


div .main > .container > .sections {
  height: 37px;
  min-width: 1200px !important;
}

div .main > .container > .sections > .section {
  margin-top: 2px;
  margin-bottom: 0px;
  height: 35px;
}
div .main > .container > .sections > .profile {
  width: 450px;
  min-width: 450px !important;
}

* .header .profile .option {
  padding: 0px;
}

* .header>.main .profileName {
  margin-bottom: 0px;
}

* .ninjaSearch h5 {
  display: none;
}

* .ninjaSearch .searchField .fieldWrap {
  margin-top: 5px;
}

* .mainMenu .menu li a {
  padding-top: 2px;
  padding-bottom: 2px;
}

* .mainMenu .menu li.isSelected a {
  padding-top: 2px;
  padding-bottom: 3px;
}

* .subMenu li a {
  padding-top: 2px;
  padding-bottom: 2px;
}

* .subMenu li.isSelected a {
  padding-top: 2px;
  padding-bottom: 3px;
}

* .header .logo {
  width: 245px;
  height: 20px;
  background-size: 250px auto !important;
}

* .header .ticker {
  margin: 0;
}

* .header .profile .option {
  clear: none !important;
}
}
