@-moz-document domain("lowes.com"), domain("lowes.shoplocal.com") {
/*__________________________________________________________________________________*/


.bnjbLb,
.carousel-item,
.grid-16,
.grid-25,
.item,
.jdGUye,
.s7_banner .scaled_banner_background_image,
.slick-slide,
.slick-slider.carousel-large .item,
[data-test*="product-tile-wrapper-"]
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.bnjbLb:hover,
.carousel-item:hover,
.epc-modal .epc-thumb.active,
.grid-16:hover,
.grid-25:hover,
.item:hover,
.jdGUye:hover,
.s7_banner .scaled_banner_background_image:hover,
.slick-active,
.slick-slide:hover,
.slick-slider.carousel-large .item:hover,
[data-test*="product-tile-wrapper-"]:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.slick-slider .slick-next, .slick-slider .slick-prev,
img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/


li,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.sub-nav .nav-menu li>label p,
li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

.slick-slider .slick-next, .slick-slider .slick-prev {
	opacity: .5!important;
}

.slick-slider .slick-next:hover, .slick-slider .slick-prev:hover {
	opacity: 1!important;
}
}

