css:
@-moz-document 
  url-prefix("http://gfycat.com"),
  url-prefix("https://gfycat.com")
{
  html, body
  {
    background: #1C1C1C !important;
  }
  
  section#jiffiest .content .vidWrap > video, section#viewVideo .content .vidWrap > video 
  {
    background: #1C1C1C !important;
  }
  
  section#viewVideo .content > .thumbs .col-xs-2
  {
    background: #1C1C1C !important;
  }
  
  section#viewVideo > .header > .inlineGroup .tag, section#viewVideo .content > .menuBar > .left .tags .tag, section#viewVideo .content > .menuBar > .right button, section#trending .content .thumbs .inlineGroup .tag
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  section#trending, section#viewVideo, section#jiffiest
  {
    background: #1C1C1C !important;
    color:#FAFAFA !important;
  }
  
  section#trending .mvpTrendTag .next, section#trending .mvpTrendTag .prev, section#jiffiest .mvpFeeds .next, section#jiffiest .mvpFeeds .prev, section#viewVideo .content.small .prev, section#viewVideo .content.small .next, section#viewVideo .content.big .prev, section#viewVideo .content.big .next
  {
    background: #0A0A0A !important;
  }
  
  section#viewVideo .content > .menuBar
  {
    background: #1C1C1C !important;
  }
  
  section#viewVideo .content .sidebar, section#viewVideo .content .sidebar .pnlTop ul.list-group > li
  {
    color: #FAFAFA !important;
    background: #0A0A0A !important;
  }
  
  section#viewVideo .content .sidebar .pnlMenu .btn-group .btn
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  h1, h2, h3, h4, h5, h6, section#trending .content .thumbs .title
  {
    color: #FAFAFA !important;
  }
  
  section#viewVideo .content.small .sidebar .pnlTop .btn-group > button, section#viewVideo .content.big .sidebar .pnlTop .btn-group > button
  {
    background: #1C1C1C !important;
  }
  
  section#viewVideo .content .sidebar .pnlMenu .btn-group-lg .btn.show + div.menuPane
  {
    background: #0A0A0A !important;
  }
  
  section#viewVideo .content.small .sidebar .pnlMenu .btn-group > .btn.show
  {
    background: #1C1C1C !important;
  }
  
  #modalDialog .ng-modal-dialog > .content, #modalDialog .ng-modal-dialog .footer
  {
    background: #1C1C1C !important;
  }
  
  #modalDialog .ng-modal-dialog .content .tabsWrap ul.nav > li.selected > a:hover, #modalDialog .ng-modal-dialog .content .tabsWrap ul.nav > li.fromURL > a
  {
    color: #FAFAFA !important;
    background: #0A0A0A !important;
  }
  
  #modalDialog .ng-modal-dialog .content .tabsWrap ul.nav > li > a
  {
    color: #FAFAFA !important;
    background: #0A0A0A !important;
  }
  
  #modalDialog .ng-modal-dialog .content .tabsWrap ul.nav > li.selected > a
  {
    color: #FAFAFA !important;
    background: #1C1C1C !important;
  }
  
  #modalDialog .ng-modal-dialog .content .tabsWrap ul.nav > li > a:hover
    {
      color: #FAFAFA !important;
      background: #1C1C1C !important;
    }
  
  #modalDialog .ng-modal-dialog .content .uploadPanel div.directions, #modalDialog .ng-modal-dialog .footer > .border
  {
     background: #1C1C1C !important;
  }
  
  .input-group .form-control:first-child
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
  }
  
  .input-group-btn:last-child > .btn
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  .dropdown-menu > li > a:hover
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  .dropdown-menu > li > a
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
  }
  
  .open > .dropdown-menu
  {
    background: #1C1C1C !important;
  }
  
  section#viewVideo .content > .description
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  #modalDialog .ng-modal.login .ng-modal-dialog > .content, #modalDialog .ng-modal.login #login .signUp 
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
}
