css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pirateproxy.sx") {
  
  
  footer {
    text-align: center;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  a, a:link, a:visited, a:focus {
    color: #CECECE;
    text-decoration: none;
    border-bottom: 1px dotted #000;
}
  
  #fp {
    display: block;
    margin: 0px auto 20px;
    text-align: center;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  body {
    
    color: #CECECE;
    background: none repeat scroll 0% 0% #FFF;
    margin: 10px;
    font: 0.7em/1.3em Verdana,Arial,Helvetica,sans-serif;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  thead#tableHead th {
    background: none repeat scroll 0% 0% #505050;
}
  
  
  table#searchResult {
    font-size: 1em;
    border-collapse: collapse;
    border-spacing: 1px;
    text-align: left;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  
  
  
  
  table#searchResult th, table#searchResult td {
    padding: 3px;
    border: 1px solid #4DE436;
    background-color: #2a2a2a;
    border-collapse: collapse;
    border-spacing: 1px;
}
  
  
  .detDesc {
    color: #10A7DA;
}
  
  
  
  
  h2 {
    font-size: 1.1em;
    line-height: 1.5em;
    text-align: right;
    padding: 2px 5px;
    margin: 0px;
    border-bottom: 0px solid #4D4D4D;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
    font-weight: normal;
    clear: both;
}
  
  
  #fp h1 a {
    background: url("http://i.imgur.com/V3g0R0s.png") no-repeat scroll 0% 0% transparent;
    margin: 0px auto;
    display: block;
    border: 0px none;
    width: 275px;
    height: 295px;
    outline: medium none !important;
}
  
  
  
  

}
