css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com"), domain("freehostedscripts.net") {
    .notification .avatar_frame::before, .notes .note .avatar_frame::before,
    #toaster .frame.like::before, #toaster .frame.reply::before,
    #toaster .frame.follower::before, #toaster .frame.reblog::before,
    .ui_avatar .frame::before, #webcam_container .control_btn:after,
    .search_popover .more_results_link,
    .search_popover .search_results_container .search_results_section:last-child .popover_menu_item:last-child,
    .search_popover .search_results_container .search_results_section:last-child .popover_menu_item:last-child .result,
    .search_popover .search_results_container .search_results_section:last-child .popover_menu_item:last-child .result_link,
    .post .post_info a.reblog_follow_button::before, * {
        border-radius: 0px !important;
    }

    a.reblog_follow_button, .share_social_button {
        display: none !important;
    }

    .post .post_info a.reblog_follow_button {
        font-size: 7px;
    }

    #webcam_container .control_btn, #webcam_container .snap_btn a.photo_btn,
    #webcam_container .snap_btn a.gif_btn {
        border-radius: 25px !important;
    }
}
