css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.memrise.com/home/), url-prefix(http://www.memrise.com/home/courses/)

 {

 
/* hide social buttons and goal-setter  */

.social-buttons,
.goal-setter{
  display:none !important;
}

/* make layout more compact  */

.course-progress-box{
 height: 60px;
}

.course-photo{
 height: 60px !important;
 width: 60px !important;

}


.btn-holder{
 height: 60px !important;
 width: 60px !important;
}


.img_crop{
 height: 60px !important;
 width: auto !important;
}

.stat_words_learned{
 font-size:10pt;
}
  
}
