css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.jp") {

#description,
#countries,
#ad_01,
#en_desc,
p#jp_desc,
#subscribe,
.search,
h3#headline,
#jp_desc,
ul#skyline,
#archive_menu,
#ad_02,
.other_menu,
a[href^="http://ad.gizmodo.jp/"],
#gizmodo_data{
display:none !important;
}

#body{background:none !important;}

.entry_data,
#ranking,
#ad_text,
#ads_03,
#foot_main,
#footer_copy,
#ad_04,
#extra_content,
#access_data,
#post,
#main > ul.page_navi{
display:none !important;
}


/* ******************* */
#ads_05,
#sub_contents{
display:none !important;
}

/* ******************* */

#main,
.entry_body,
body#detail .entry{
width:auto !important;
float:none !important;
}

h1 a[href="http://www.gizmodo.jp/"]:after{
content:"GIZMODE JAPAN";
}

h1 img {display:none !important;}

#head_main {
height:auto !important;
width:auto !important;
background:none !important;
}

#head .archive_lists {
position:relative !important;
float:right !important;
}

}
