css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("btc-flow.com") {
.animation
  {display: none;}

  .header
  {display: none;}

  .sidebar
  {display: none;}

  .levelup
  {display: none;}

  .footer
  {display: none;}

  .text
  {display: none;}

  .deposit
  {display: none;}

  .mainblock .lifetime
  {display: none;}

  .bitcoin
  {box-shadow: 0px 0px 0px}

  .bitcoin:after
  {display: none;}

  .mainblock h1
  {display: none;}

  .index .image img
  {display: none;}

  .index .image p
  {display: none;}

  .index .maincontent
  {padding: 0px 0px;}
  
  .index form
  {height: 80px;}

  .bitcoin
  {height: 40px;}

  .index main
  {display: block;}

  .mainblock
  {margin: 0px 0px 0px;}

  .index .buttons
  {height: 88px; padding: 0px 35px 2px 35px;}

  .index .button
  {height: 88px;}
}
