css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtropes.org") {
  .container h2{ background:none; }
	.forumreplybody h2 {
			position:relative;
			clear:left;
			background-color:#eeeeff;
			margin-top:6px;
			margin-left:8px;
			margin-bottom:6px;
			padding:4px;
			border-radius:8px;
			-moz-border-radius:8px;
			-webkit-border-radius:8px;
			font-size:small;
			color:#D2691E;
  }
  #wikitext h2{
		position: relative;
    		clear: left;
    		background-color: #EEF;
    		margin-top: 6px;
    		margin-left: 8px;
    		margin-bottom: 6px;
    		padding: 4px;
    		border-radius: 8px;
    		font-size: medium;
  }
}
