css:
/* by fns_aviator */

@-moz-document url-prefix("http://www.fornesia.com/") {
body{
  background:#1A1A1A url(https://lh4.googleusercontent.com/-8RYFTpzzwn4/UXCUe9tsSgI/AAAAAAAAo0M/eNLUAOorumc/w1440-h961-p-e31536000-k/dinascrowcastle-2.JPG) repeat fixed top center!important;
  background-attachment:fixed!important;
  color:#aaaaaa!important;
  font:8pt Tahoma,verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif!important;
  margin:0!important;
  padding:0!important
}

.label_green {border-radius: 6px 6px 6px 6px !important;}
.label_blue {border-radius: 6px 6px 6px 6px !important;}
.label_purple {border-radius: 6px 6px 6px 6px !important;}
.label_yellow {border-radius: 6px 6px 6px 6px !important;}
.label_orange {border-radius: 6px 6px 6px 6px !important;}
.second_menu .menu_button {border-radius: 6px 6px 6px 6px !important;}
.nav_menu > ul > li > a:hover {border-radius: 14px 14px 6px 6px !important;}
.tools > a {border-radius: 8px 0px 0px 8px !important;}
    
.page {
  margin: 0px auto !important;
}

.header {
  text-align: center;
}
  
.panel {
  background: rgba(35,35,35,.1) !important;
  box-shadow: 0px 0px 15px #232323;
}

.footer {
  background: rgba(35,35,35,.8) !important;
}
  
.container {
  background: rgba(35,35,35,.5) !important;
  border: 1px solid rgba(10, 10, 10, 0.500) !important;
  border-radius: 14px 14px 6px 6px !important;  
  box-shadow: 0px 0px 25px #232323;
}
  
table {
  color: #ffffff !important; 
  font-family: "georgia";
  font-size: 13px;
}

.nav_menu{
  background: rgba(35,35,35,.65) !important;
  border-radius: 14px 14px 0px 0px !important;
}

.second_menu {
  background: rgba(35,35,35,.77) !important;
  border-radius: 0px 0px 14px 14px !important;
}

.menu {
  background: rgba(35,35,35,.5) !important;
  border-radius: 14px 14px 14px 14px !important;  
}
  
.postbit_column {
  background: rgba(19,19,19,.5) !important;
}

.postbit_footer {
  background: rgba(19,19,19,.5) !important;
}

.tborder {
  background: rgba(27,27,27,.5) !important;
}

.tcat, .trow_sep {
  background-image: -webkit-linear-gradient(right,rgba(0,0,0,.5),rgba(0,0,0,0),rgba(0,0,0,.5)) !important;
}

.fns_user {
  background:transparent !important;
  background-image: -webkit-linear-gradient(right,rgba(0,0,0,.5),rgba(0,0,0,0),rgba(0,0,0,.5)) !important;
}

.trow1, .trow2 {
  background:transparent !important;
  background-image: -webkit-linear-gradient(right,rgba(22,22,22,.6),rgba(0,0,0,0),rgba(22,22,22,.6)) !important;
}

.breadcrumb {
	opacity: 0.7;
}

blockquote, .codeblock {
    background: rgba(ff,0f,0f,.9) !important;
	filter: alpha(opacity=70);
	opacity: 0.7;
}

.index_stats {
  background-color: transparent;
}

.tfoot {
  background-image: -webkit-linear-gradient(right,rgba(22,22,22,.6),rgba(0,0,0,0),rgba(22,22,22,.6)) !important;
}
}
