css:
@-moz-document url-prefix("http://www.mengwubaike.net") {
    [class*="eis_body_"] {
       background:none !important;
    }
    body[id*="nv_"] {
       background:url('http://img.itc.cn/photo/oX1kMBpXTh1') no-repeat fixed center center;
       background-size:100% !important;
    }
    DIV#eis_nv {
        margin-left:0.32%;
        padding-left:0.32%;
        width:99.07%;
        background-image:none;
        background:rgba(0, 0, 0, 0.41);
    }
    DIV.eis_nv,.eis_nv ul {
        background:none;
    }
    DIV.eis_nv ul form {
        margin-top:11px;
        background:rgba(246, 246, 246, 1.0) !important;
        padding:0px 0px 0px 0px;
        border:2px solid;
        border-radius:25px;
     }
    DIV.eis_nv ul form BUTTON::before {
        content:"我搜~";
    }
    ul form button {
        background:rgba(0, 0, 0, 0.48) !important;
        border-radius:25px;
        width:48px !important;
        height:18px !important;
        margin-top:3px !important;
        margin-left:1.5px !important;
        color:#fff !important;
    }
    
    DIV.eis_nv ul form INPUT#eskey[type="text"] {
        margin-left:5px;
        margin-top:2px;
        
        width:146px;
    }
}
