css:
@-moz-document domain("domivka.net") {
    html {
        background-color: black;
    }
    
    body, body a {
        filter: invert(100%);
    }
    
    #navtabs {
        background: #000;
    }
    
    .above_body, #footer {
        filter: invert(100%);
    }
    
    .floatcontainer {
        vertical-align: middle;
    }
    
    .floatcontainer a {
        color: white;
    }
    
    .postcontent > img {
        filter: invert(100%);
    }
}
