css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https?://video.aktualne.cz/.*") {
body, .obsah, .obsah.detail {
    background-color: #1e1e1e !important;
}

.subscribe-box {
    opacity: .55 !important;
}

.subscribe-box h3 {
    background: #6F020A !important;
    color: #e3e3e3 !important;
}

.hlavicka .logo a {
    opacity: .8 !important;
}

.slimScrollBar {
    margin: 0 !important;
    right: 0 !important;
}

.slimScrollRail {
    background-color: #0F0F0F !important;
    border-width: 0 !important;
}

.hlavicka .menu ul.tlacitka > li, .hlavicka .hledani input[type="text"] {
    border-color: #3F3F3F !important;
    color: #C8C8C8 !important;
}

.hlavicka-obal.podstranka {
    background-color: #0F0F0F !important;
    color: #C8C8C8 !important;
}

.active {
    background-color: #0F0F0F !important;
    color: #C8C8C8 !important;
}

body, a, h1, h2, h3, p, .nazev {
    color: #C8C8C8 !important;
}

div.stin {
    display: none !important;
}

div.playlist-title-inner h4 {
    background-color: #1e1e1e;
}

.boxik h5 {
    color: #C8C8C8 !important;
}

.paticka {
    background-color: #1e1e1e!important;
}
}
