@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ololo.fm") {
  body {background: url(http://ya-vidoc3000.narod.ru/anime/9.jpg) no-repeat fixed; -webkit-background-size: cover; background-size: cover; color: black;}
    .block_right_alt, #vkwidget1, .yashare-auto-init, .block_right_alt2, #ad_ph_1, .versus_block, .block3, #DIV_DA_125118, .block2, #wall, #vkwidget2_tt, .artist_fact, #links, .logo, .panda, .top_menu, .header2, .notice, .newfooter, .yashare-auto-init, .footer, .tabs, .alert {display: none;}
  .bl_right2 .search {background: none; opacity: 0.7; position: relative; margin-right: 280px;}
  .header {background: none; height: 50px;}
  .col_2, .image {opacity: 0.5;}
  .col_2:hover, .block:hover, .image:hover {opacity: 1.0;}
  H1, .block {background-color: white; opacity: 0.7;}
  #player .main {background: none !important; background-color: white !important; color: black;}
  #trackname {color: red;}
  .progress {background-color: white !important; background-image: linear-gradient(to bottom, white, white) !important;}
  #player .main a {color: red;}
  .noUiSlider.connect a, .noUiSlider.connect.lower {background: none repeat scroll 0% 0% red;}
  #player .sub {background: none !important; background-color: white !important;}
  #links {display: none !important;}
}
