@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com") {

/*sets semi-transparent background for googlepedia parts*/
.infobox, .nobr,#wikibox #catlinks, #wikibox,.shrunk,#toc, .toc, .mw-warning{background:transparent url(http://lh3.google.com/preston.scheuneman/R1m-ja98gmI/AAAAAAAAADg/omOHyQ--TtE/20pct+Black.png) repeat scroll 0 0!important}

/*changes some googlepedia font colors to fit better*/
#wikibox h1, #wikibox h2, #wikibox h3, #wikibox h4, #wikibox h5, #wikibox h6 {color:#eeeef3!important}

/*sets up the image for the google nav bar*/
.csb, #np, #nf, #nc, #nn, #nl, .nr, .ss {background:transparent url(http://i64.photobucket.com/albums/h180/ragingshinigami/igoogle-1.png) no-repeat scroll 0 0!important; display:block; height:26px;}

/* sets the proper portions of the image to be displayed*/
#nav #nf {background-position:-4px -6px!important; width:26px!important;}
#nav #nc,#nav .nr {background-position:-29px -6px!important;width:15px!important}
#nav #nn {background-position:-57px -6px!important;width:66px!important}
#nav td {text-align:left!important;padding:1px!important;font-family:times new roman!important}

/*fixes a logo glitch for google images*/
.csb, .n div, #logo span {background:transparent ; display:none!important;}

/*fixes some padding issues*/
*, .n .i, .b a{padding:1px!important}
}
