css:
@-moz-document url-prefix("http://getbootstrap.com"), url-prefix("http://v4-alpha.getbootstrap.com") {
body {
  padding-top: 50px;
}

.bs-docs-nav,
.bd-navbar {
  position: fixed;
  right: 0;
  left: 0;
  top: 0;
  border-bottom: 0;
  border-radius: 0;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.25);
  background-color: #fff;
  z-index: 1030;
}

.bs-docs-sidebar.affix {
  top: 60px;
}

/* Hide Back-to-top anchor */
@media (min-width: 768px) {
  .back-to-top {
    display: none;
  }
}
}
