css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

#posts .post.new_post 
{
min-height: 124px !important;
}

.new_post_label/*_icon*/ > i
{
background-image: url('http://41.media.tumblr.com/7e80f10b92df74eb76854c414431c136/tumblr_nkcmij1dN81s7f71lo2_r2_540.png') !important;
background-repeat: no-repeat !important;
}

.new_post_label
{
padding: 62px 1px 5px 0px !important;
}


.new_post_label i
{
height: 95px !important;
}

.new_post_label > i 
{
color: transparent !important;
}


#new_post_label_text,#new_post_label_photo,#new_post_label_quote,#new_post_label_link,#new_post_label_chat,#new_post_label_audio,#new_post_label_video
{
padding-top:98px !important;
} 

#new_post_label_text:hover,#new_post_label_photo:hover,#new_post_label_quote:hover,#new_post_label_link:hover,#new_post_label_chat:hover,#new_post_label_audio:hover,#new_post_label_video:hover
{
font-weight:bold !important;
} 

#new_post_label_text > i
{
background-position: 3px -2px !important;
}


#new_post_label_photo > i
{
background-position: -73px -2px !important;
}


#new_post_label_quote > i
{
background-position: -150px -2px !important;
}


#new_post_label_link > i
{
background-position: -226px -2px !important;
}


#new_post_label_chat > i
{
background-position: -302px -2px !important;
}

#new_post_label_audio > i
{
background-position: -378px -2px !important;
}


#new_post_label_video > i
{
background-position: -456px -2px !important;
}



#new_post_label_text
{
color: rgba(109, 109, 110, 1) !important;
}
#new_post_label_photo
{
color: rgba(223, 129, 106, 1) !important;
}
#new_post_label_quote
{
color: rgba(243, 177, 91, 1) !important;
}
#new_post_label_link
{
color: rgba(110, 198, 150, 1) !important;
}
#new_post_label_chat
{
color: rgba(109, 184, 222, 1) !important;
}
#new_post_label_audio
{
color: rgba(186, 151, 204, 1) !important;
}
#new_post_label_video
{
color: rgba(152, 161, 168, 1) !important;
}

}
