@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.cool3c.com") {
  
#page, #sidebar-article{
    background: rgb(48,48,48) !important;
    color: rgb(210,210,210) !important;
}

.node-serial-item {    
    background: rgb(48,48,48) !important;
    color: rgb(210,210,210) !important;
}
.node-serial-item .submitted{    
    color: rgb(210,210,210) !important;
}
    
    
    
.article-item a:link, .node-serial-item .content{
    color: rgb(210,210,210) !important;
}
  
.article-item a:hover, .article-active a{
   background: rgb(0,0,0) !important;
   color: #FF9900 !important;
}  

   
.post-list-inner{
   background: rgb(60,60,60) !important;
   color: #FF9900 !important;    
}
   
.post-list-title a{
   color: #FF9900 !important;    
}   
    
.post-list-body a{
   color: rgb(210,210,210) !important;
}

.post-list-type {
    background: rgb(100,100,100) !important;
}   

.author-inner {
    background: rgb(60,60,60) !important;
    color: rgb(210,210,210) !important;
}      
   
.comment-wrapper {
    background: rgb(60,60,60) !important;
    color: rgb(210,210,210) !important;  
}   
   
.spacing-section {
    background: rgb(60,60,60) !important;
    color: rgb(210,210,210) !important;  
}   
   
.spacing-header {
    color: rgb(210,210,210) !important;  
}   

a {
  color: #FF9900 !important;
}  
    
a:visited{
  color: #ff9999 !important; 
}
    
}
