css:
@-moz-document url-prefix("https://chamilo.hogent.be/"), url-prefix("https://www.facebook.com/") {
body {
 	background-color: #FE1F6F; 
}
#header1{
  background: #FE1F6F;
}
.banner{
    display: none;
}
  
:hover{
animation: animationFrames linear 0.24s;
  animation-iteration-count: infinite;
  transform-origin: 50% 50%;
  -webkit-animation: animationFrames linear 0.24s;
  -webkit-animation-iteration-count: infinite;
  -webkit-transform-origin: 50% 50%;
  -moz-animation: animationFrames linear 0.24s;
  -moz-animation-iteration-count: infinite;
  -moz-transform-origin: 50% 50%;
}

@keyframes animationFrames{
  0% {
    transform:  translate(0px,0px)  ;
  }
  28% {
    transform:  translate(33px,-74px)  ;
  }
  72% {
    transform:  translate(-103px,47px)  ;
  }
  100% {
    transform:  translate(-1px,1px)  ;
  }
}
@-moz-keyframes animationFrames{
  0% {
    -moz-transform:  translate(0px,0px)  ;
  }
  28% {
    -moz-transform:  translate(33px,-74px)  ;
  }
  72% {
    -moz-transform:  translate(-103px,47px)  ;
  }
  100% {
    -moz-transform:  translate(-1px,1px)  ;
  }
}
@-webkit-keyframes animationFrames {
  0% {
    -webkit-transform:  translate(0px,0px)  ;
  }
  28% {
    -webkit-transform:  translate(33px,-74px)  ;
  }
  72% {
    -webkit-transform:  translate(-103px,47px)  ;
  }
  100% {
    -webkit-transform:  translate(-1px,1px)  ;
  }
}
}
