css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kulturegeek.fr") {

#big-slider, .menu, .side-content, .test_home2, .test_home, .site-bottom, .pub_partage, .yarpp-related, .enp-report, .page-path, .adsPost, .adsHome, .regles_comment, .rss_coms, .page-header, .simple-divider, .author, .share, .site-header, #socialbar {
	display:none!important;
}

body {
	background:#f8f8f8;
}

.main-content {
	width:100%;
}

.site-block {
	width:100%!important;
	border-bottom: 11px solid #f16022 !important;
}

.site-center {
	margin-top:30px;
	padding: 0 7%!important;
}

a.button-white-arrow-right:hover, a.button-dark-grey-arrow-right {
	display:none;
}

img.post_img.wp-post-image {
	margin:10px auto 40px auto!important;
}

.post-name {
	margin-top:20px!important;
}

.main-content {
	margin-top: 10px;
}

@media screen and (max-width: 650px) {
	.site-center {
        	padding: 0 10px !important;
    }
}

@media screen and (min-width: 650px) and (max-width: 720px) {
	.site-center{
        	padding: 0 3% !important;
    }
}

@media screen and (min-width: 720px) and (max-width: 820px) {
	.site-center {
        	padding: 0 5% !important;
    }
}

@media screen and (max-width: 680px) {
	img.post_img.wp-post-image {
        	width: 100% !important;
		height:auto;
        	margin-left: 0 !important;
    }

	.columns img {
		width: 95% !important;
		height:auto;
	}

	.columns iframe {
		width: 95% !important;
	}
}

}
