css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.raagalahari.com") {

body, table {background-color: #202020 !important;}

.white ul.mega-menu {background: url("http://icdn.raagalahari.com/css/skins/images/bg_black.png") repeat-x scroll 0 -80px #ccc;}

.white ul.mega-menu li a {background: url("http://icdn.raagalahari.com/css/skins/images/bg_black.png") repeat-x scroll 100% 0 rgba(0, 0, 0, 0);
    color: #fff;text-shadow: 0 0 0 #fff;}

#ctl00_fbcomments {display:none}
  
  #pagingCell a, .wordPages {color:#707070;}
  
  #ctl00__pagecontent_divgaltitle {color:white;}
  
  #pagingCell span.currentPage {color:whitesmoke;}
  .copyright, .addthis_toolbox, #rightzone, #leftzone {display:none;}
  #middlezone {margin:auto !important;}
  .galthumbnew {margin: 2px !important;height: 220px !important;}
td#pagingCell {
    text-align: right;
    padding-bottom: 10px;
    padding-right: 10px;
    position: fixed;
    right: 0;
    top: 180px;
    background: black;
    padding: 10px;
}
#relatedgal-link {position: fixed;
    font-weight: bold;
    color: #ee0000;
    right: 0;
    background: black;
    padding: 10px;}
}
