css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.meteor.com") {
  /* who likes it */
  .topic-body > section.post-actions {
    position: absolute;
    top: 2px;
    left: 890px;
    width: 210px;
    transform: scale(0.9);
  }
  /* reply as linked topic */
  article .gutter {
    transform: scale(0.9);
    padding-left: 780px;
  }
  /* reply button */
  .contents .post-menu-area {
   margin:0px;
  }
  /* action buttons */
  .contents .post-menu-area .post-controls .actions {
    position: absolute;
    bottom: 1px;
    left: 650px;
    width: 300px;
    transform: scale(0.8);
    margin: 0;
  }
  /* post body */
  div.topic-body {
    padding: 2px 11px;
  }
  div.topic-body .contents {
    margin-top: 5px;
  }
}
