@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rp5.ru") {
  html, body {background: url(http://ya-vidoc3000.narod.ru/anime/3.jpg) fixed; -webkit-background-size: cover; background-size: cover;}
  #wrapper, #header {background: none !important;}
  /*Убираем рекламу и подвал*/
  #footerWrapper {display: none !important;}
  /*Шапка*/
  #topMenuContent, #archiveString {background: none !important;}
  #topWrapper {background: none;}
  .extremumWrapper, .extremumFooter, #forecastShort, #forecastTable .forecastDate {background: none !important; background-color: grey !important; opacity: 0.7; color: white !important;}
  /**/
  #content {width: 1200px !important;}
  #forecastHeader {margin: 0 !important;}
  #forecastHeaderContent {background: none !important; background-color: grey !important;}
  .brdDateRed {background: none !important; background-color: red !important;}
  .brdDateLight {color: white !important;}
  .countryMap {background-color: grey !important; color: white !important; opacity: 0.7;}
  #content .countryMap a {color: white !important;}
  #content .countryMap .letter {background: none !important; background-color: black !important;}
  #content a {color: white !important;}
  h1 {color: white !important;}
  .extremumFooter .zoneLeft {background: none !important;}
  #wrapper #container div.noprint {display: none !important;}
  #forecastHeader {display: none !important;}
  .MainTemp {background-color: black !important;}
  #forecastTable a {color: black !important;}
}
