css:
@-moz-document domain("twitter.com") {
.tweet .time { 
	float: right;}

.global-nav-inner {
background: #202020;
height: 46px;
}

.bird-topbar-etched {
color: #55ACEE !important;
}

.nav>li {
color: #DDDDDD;
}

.global-nav .form-search .search-input {
background-color: rgb(90, 90, 90);
}
      
      .global-nav .form-search .search-input.focus {
background-color: #6f6f6f;
opacity: 1;
-ms-filter: "alpha(opacity=100)";
}

.tweet-btn, .tweet-btn:focus { 
background: #55ACED;
}

#global-new-tweet-button { 
opacity: 1;
}

.nav.right-actions > li > a, .nav.right-actions > li > button { 
opacity: 1;
}

.global-nav .container {
text-align: center;
width: auto;
max-width: 920px;
}

.AppContainer {
max-width: 920px;
}

.nav.right-actions > li > a, .nav.right-actions > li > button { 
margin: 0px 0px 0px 15px;
}

.wrapper, .wrapper-narrow, .wrapper-permalink {
background: url("https://abs.twimg.com/a/1391118865/img/t1/wash-white-30.png") transparent;
margin: 0px auto;
min-height: 100%;
position: relative;
width: 890px;
padding: 56px 15px 15px;
}

body.three-col .content-main { 
float: left;
margin: 0px 0px 0px 10px;
}

body.three-col .wrapper { 
width: 890px;
}

div[class="dashboard dashboard-right"] {display:none!important;}

.global-nav .search-input { 
color: #FFF!important;
}

.navy-seal-experiment .global-nav .search-input {
border: none!important;
}

div[class="Grid-cell u-size1of3"]{display:none!important;}

.u-lg-size2of3 {
width: 100%!important;
}
}
