@-moz-document domain("technews.tw") {   
 
body, .widget-area .widget ,.rps_wrapper{
    background: rgb(48,48,48) !important;
    color: rgb(210,210,210) !important;
}

div{
    color: rgb(210,210,210) !important;
}
    
