    color: rgb(240,0,100) !important;
}   

a, .rps_item a{
    color: #FF9900 !important;
} 

a:visited{
  color: #ff9999 !important;
}    
    
a:hover{
   color: #CC6633 !important;
}
    
.abh_tab_content{
    background: rgb(48,48,48) !important;}
    
.entry-content{
