css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kiamagic.com") {
body {
  background: #000 !important;
}
  .kiabanner_logo {
    display: none;
  }
  span.kiabanner_title {
    text-align: center !important;
    font-size: 1.2em !important;
    font-weight: bold;
    font-family: monospace !important;
    background: #000;
    padding: 0px !important;
    display: block;
    margin-left: auto;
    margin-right: auto;
  }
  div.kiabanner_menu {
    margin-top: -10px;
    text-align: center !important;
    font-size: 1.1em !important;
    font-weight: bold;
    font-family: monospace !important;    
  }
  div.kiabanner_menu span {
    font-size: 1.1em !important;
    font-weight: bold;
    font-family: monospace !important; 
  }
  div.kiabanner_menu span a {  
    background: #000;
  }
  div#kia_admin_bar {
    background: #000 !important;
    background-image: none;
    width: 100%;
    min-height: 30px;
  }
  div#kia_admin_bar ul {
    display: block;
    background: #000 !important;
  }
  div#kia_admin_bar ul li {
    background: #000 !important;
  }
  div#bp-before-header {
    background: #000 !important;    
  }
  div#bp-before-container {
    background-image: url(http://kiamagic.com/wiki/images/thumb/e/e5/KIA_Logo2b.jpg/120px-KIA_Logo2b.jpg) !important;    
  }
  div#container {
    width: 99% !important;
  }
  div#container .item-list-tabs {
    background: transparent !important;    
    border: none;
  }
  #sidebar-squeeze {
    width: 90%;
  }
  div#container .item-list-tabs ul li {
    background: #000 !important;    
  }
  body.single div#content, body.page div#content {
    width: 95%;
    border: 1px solid #333 !important;     
    background: #000 !important;
    margin-bottom: 100px;
  }
  div#globalWrapper div#content {
    border: 1px solid #333 !important;     
    background: #000 !important;
    margin-bottom: 100px;
    margin-top: 10px;
    margin-right: 10px;
  }
  div.portlet {
    border: 1px solid #333 !important;    
    background: #111 !important;   
    margin-bottom: 20px;
  }
  div#Body {
    width: 95%;
    border: 1px solid #333 !important;     
    background: #000 !important;
    margin-bottom: 100px;
    margin-top: 10px;
    margin-left: 50px;
    margin-right: 50px;
  }
  div#Frame, div#globalWrapper {
    background-image: url(http://kiamagic.com/wiki/images/thumb/e/e5/KIA_Logo2b.jpg/120px-KIA_Logo2b.jpg) !important;    
  }
  body.single div#content div#main-column, div#content div#main-column {
    background: #000 !important;
  }
  body.single div#content div.padder, div#content div.padder {
    margin-right: 0px !important;
  }
  div.yarpp-related {
    background: #111;
  }
  div#bp-before-footer {
    background: #000 !important;    
  }
}
