css:
/*======================================
  FNTNRIJ liquid compact 2015/03
  @-moz-document domain("eigokiji.cocolog-nifty.com")
  ======================================*/
@-moz-document domain("eigokiji.cocolog-nifty.com") {
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: initial !important;
 font-weight: initial !important;
 line-height: 1.4em !important;
 }
[id$="-inner"] {width: auto !important;}

/*--- header ---*/
#banner {height: auto !important;}
#banner-inner {padding-top: 1em !important;}

/*--- footer ---*/


/*--- main contents ---*/
#alpha {width: 100% !important; margin: 0 -150pt 0 0 !important;}
#alpha>* {width: auto !important; margin: 0 150pt 0 0.5em !important;}
#beta {width: 150pt !important;}
#beta * {max-width: 150pt !important;}

.entry-header {margin: 0 !important;}
.entry-header, .entry .entry-content {font-size: medium !important;}
.entry-body p {text-indent: 1em;}
.entry-body p:first-child br {display: inline-block !important; width: 1em !important;}
}
