css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jeuxvideo.com") {
  .user-avatar-msg {
            border-radius:50%;
            border: 1px turquoise solid;
            /* animation */
                
                transition: all 0.4s ease-out;
                transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -webkit-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -moz-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -o-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -ms-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
        }
        .user-avatar-msg:hover{
              /*
              CHANGE ANIMATION SPEED HERE,
              I like using a slow one but putting it on "1s" will work fine too, and with no bug, try replacing the speed by the value you want
              */
             animation: pp 1s infinite; /* <-- IT'S HERE, DEFAULT IS "5s" */
        }
        @keyframes pp {
          50% { 
                transform: rotate(-26deg) scale(1) skew(1deg) translate(0px);
                -webkit-transform: rotate(-26deg) scale(1) skew(1deg) translate(0px);
                -moz-transform: rotate(-26deg) scale(1) skew(1deg) translate(0px);
                -o-transform: rotate(-26deg) scale(1) skew(1deg) translate(0px);
                -ms-transform: rotate(-26deg) scale(1) skew(1deg) translate(0px);}
          100%  {
                transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -webkit-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -moz-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -o-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
                -ms-transform: rotate(26deg) scale(1) skew(1deg) translate(0px);
          }
        }
      .content-img-avatar div img{transition: all 0.3s ease-out;transform: rotate(15deg) scale(1) skew(1deg) translate(0px);-webkit-transform: rotate(15deg) scale(1) skew(1deg) translate(0px);-moz-transform: rotate(15deg) scale(1) skew(1deg) translate(0px);-o-transform: rotate(15deg) scale(1) skew(1deg) translate(0px);-ms-transform: rotate(15deg) scale(1) skew(1deg) translate(0px);border-radius:50%}
.content-img-avatar:hover div img {transition: all 0.5s ease-out;transform: rotate(0deg) scale(1) skew(1deg) translate(0px);-webkit-transform: rotate(0deg) scale(1) skew(1deg) translate(0px);-moz-transform: rotate(0deg) scale(1) skew(1deg) translate(0px);-o-transform: rotate(0deg) scale(1) skew(1deg) translate(0px);-ms-transform: rotate(0deg) scale(1) skew(1deg) translate(0px);}
}
