css:
/***
InoReader Ntm Tweaks (v0.33)
by: Newtomic
https://userstyles.org/styles/111341
***/
@-moz-document domain("inoreader.com") {
/* articles lines */
.ar {
	margin: 0 0 0 1px !important;
	padding: 0 3px 0 1px !important }
/* read marker1s */
.article_stripe {
	margin: -3px 0 -3px -4px !important;
	width: 10px }
/* favicon feed (article list) */
.article_favicon {
	margin: 0 2px 0 118px }
/* site feed title */
.article_feed_title {
	margin: 0 -6px 0 0 !important }
/* feed article title */
.article_header_title {
	border-left: rgba(128,128,128,0.5) dotted 1px;
	margin: 0 -5px 0 0;
	padding-left: 3px }
/* right infos (group) */
.arrow_div {
	height: 16px;
	right: auto; /* repositioning to the left */ }
/* right infos - date */
.header_date {
	left: 66px;
	position: absolute }
/* right infos - star */
.star_img.icon16.icon-star_empty {
	/* just a placeholder */ }
/* right infos - read radio check */
.icon16.icon-radio_checked_big, .icon16.icon-radio_unchecked_big {
	left: 0;
	position: absolute }
/* right infos - open externally */
.icon16.icon-new_tab {
	left: 50px;
	position: absolute }
/* right infos - 3dots */
.icon16.icon-more_horizontal_dots {
	left: 34px;
	position: absolute }
/* hovers */
.article_stripe:hover {
	background-color: rgba(255,128,0,0.9) !important }
.article_header_title:hover, .article_title_link:hover/*inside article title*/,
.star_img.icon16.icon-star_empty:hover,
.icon16.icon-radio_checked_big:hover, .icon16.icon-radio_unchecked_big:hover,
.icon16.icon-new_tab:hover,
.icon16.icon-more_horizontal_dots:hover {
	color: rgba(255,128,0,0.9) }
}
