css:
@-moz-document url("about:newtab") {

/* SCROLLBOX */
#newtab-scrollbox:not([page-disabled]) {
    color: #FFFDFD;
    /*background-color: #0F0F0F;*/
    background: linear-gradient(rgba(16,16,16,1), rgba(48,48,48,1)) repeat scroll 0% 0% padding-box transparent;  
}

#newtab-search-submit {
    height: 38px;
    font-size: 13px !important;
    -moz-margin-start: -1px;
    background: linear-gradient(rgba(255, 177, 59, 0.99), rgba(251, 127, 6, 0.84)) repeat scroll 0% 0% padding-box transparent;
    padding: 0px 9px;
    border-width: 2px;
}
  
#newtab-search-text {
    height: 38px;
    -moz-box-flex: 1;
    padding: 0px 8px;
    background: none repeat scroll 0% 0% padding-box rgba(0, 0, 0, 0.9);
}
  
#newtab-search-text {
box-shadow: 1px 2px 10px rgba(28, 134, 248, 0.76) inset, 0px 0px 2px rgba(8, 22, 37, 0.1) inset, 0px 1px 0px rgba(255, 255, 255, 0.2);
border-radius: 2.5px 12px 0px 2.5px;  
}  
  
/* SCROLLBOX */
#newtab-scrollbox:not([page-disabled]) {
    color: #777;
    background-color: #222;
}  
  
#newtab-intro-what, .newtab-sponsored, .newtab-title {
    color: #FAA736;
    text-shadow: 3px 1px 3px rgba(255, 	140, 	000, 1), 0 0 1em rgba(255, 	140, 	000, 1);
}
    
.newtab-title {
    font-size: 14px;
}   
 
.newtab-site {
    transition-property: top, left, opacity, box-shadow, background-color;
    box-shadow: 10px 2px 0px #F09B2B;
}
    
.newtab-site {
    position: relative;
    -moz-box-flex: 1;
    transition-duration: 300ms;
    transition-timing-function: ease-out;
}
    
}
