css:
@-moz-document regexp("https?://([^.]+[.])*google[.].*") {
#gb a[target="_blank"], #gb a[aria-expanded] { display: none !important }
div.rs.k2 div.oa[title^="Hangouts"] { display: none !important; } /* Inbox */
#gb a[aria-expanded][title^="Google Account"] { display: block !important; }
#gb a[aria-expanded][href^="https://accounts.google."] { display: block !important; }
}
