css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {


.controls_section li .posts:before {position:absolute!important; height:30px!important; width:130px!important; line-height:28px!important; font:inherit!important; left:40px!important;}

a.posts div.hide_overflow {visibility:hidden!important;}

.controls_section li .posts:before {content: "Datas"!important;}


.controls_section li .followers:before {position:absolute!important; height:30px!important; width:180px!important; line-height:28px!important; font:inherit!important; left:40px!important;}

a.followers div.hide_overflow {visibility:hidden!important;}

.controls_section li .followers:before {content: "Irrelevants"!important;}
}
