css:
@-moz-document domain("listentoyoutube.com") {
body {
background: #222;
font: 71%/1.5em Verdana, Tahoma, arial, sans-serif;
color: red;
text-align: center;
margin: 0px;
}

#wrap {
width: 1100px;
background: #222;
/* Color: red; */
margin: 0 auto;
text-align: left;
}

input {
padding: 3px;
border: 1px solid red;
background: #222;
font: normal 1em Verdana, sans-serif;
color: red;
}

h1, h2, h3, p {
margin: 10px 15px;
padding: 0;
color: red;
background: #222;
}

#header {
position: relative;
height: 150px;
background: #222;
padding: 0;
color: red;
}

#sidebar {
float: right;
width: 300px;
padding: 5px;
margin: 0px 10px 0px 0px;
color: red;
background: #222;
}

form {
margin: 10px 10px 10px 0px;
padding: 10px;
border: 1px solid red;
background: #222;
}

#sidebar ul.sidemenu li {
list-style: none;
padding: 4px 0 4px 10px;
margin: 0 2px;
color: red;
border-bottom: 1px solid red;
}

#sidebar ul.sidemenu {
list-style: none;
text-align: left;
margin: 7px 10px 8px 0;
padding: 0;
text-decoration: none;
border-top: 1px solid red;
}

#header h1#logo-text a {
position: absolute;
margin: 0;
padding: 0;
font: bolder 24px 'Trebuchet MS', Arial, Sans-serif;
color: red;
text-transform: none;
text-decoration: none;
background: transparent;
top: 60px;
left: 40px;
}

#header p#slogan {
position: absolute;
margin: 0;
padding: 0;
font: bold 11px 'Trebuchet MS', Arial, Sans-serif;
text-transform: none;
color: red;
top: 40px;
left: 40px;
}

#header #header-links a {
color: red;
text-decoration: none;
}

#main h2 {
font: normal 180% 'Trebuchet MS', Tahoma, Arial, Sans-serif;
padding: 0;
margin-bottom: 0;
color: red;
}

#footer {
color: red;
background: #222;
clear: both;
width: 1070px;
height: 57px;
text-align: center;
font-size: 90%;
margin-left: 15px;
}

#menu {
clear: both;
margin: 0 auto;
padding: 0;
background: #222;
font: bold 16px/40px "trebuchet MS", Arial, Tahoma, Sans-serif;
height: 40px;
width: 1070px;
}

#menu ul li a {
display: block;
float: left;
padding: 0 12px;
color: red;
text-decoration: none;
background: #222;
}

tr {
background: #222;
}
}
