/* i really want this to be global */
.wide_wall_module .wall_post_text, .wide_wall_module .wall_reply_text {

text-align: right;
  direction: rtl;
}

.wall_post_text , .reply_text{
  direction: rtl;

}

.wall_module .post_info {
 margin-left: 0px;
      text-align: right;
      
    }

    .wall_module .reply_link_wrap {

      text-align: left;}
    
    .wall_module div.post_image {

      right: 8px;}
    
    .wall_text_name {

text-align: right;
}
    
    .wall_module a.reply_image {

right: 0;
    }
    
    .wide_wall_module .reply_info {
          margin-left: 0px;}


.wall_module .reply_info {
  margin-left: 0px;}

.wide_wall_module .like_wrap, .wide_wall_module .reply_link_wrap, .wide_wall_module .reply_link_wrap small, .wide_wall_module .info_footer {

  text-align: left;}

#pb_text {

    direction: rtl;
    text-align: right;
}
.wall_module .reply_box_open .reply_form {

direction: rtl;
text-align: right;
}
.wall_text {

margin-right: 25px;
}

.pads {
  background: #BECCD2;}
  

  .wide_wall_module .wall_text, .wide_wall_module .wall_post_text {
width: 97%;
}
  
  .post_full_like {

right: 30px;
}

#wrap3 {
background: white;
}

.post_table {

 border: 2px solid rgb(0, 158, 255);
  padding: 5px;
  margin: 5px;
}
